import "core-js/modules/es.array.includes.js";
export default {
  name: "ProductsView",

  data() {
    return {
      search: ''
    };
  },

  computed: {
    products() {
      return this.$store.state.products;
    },

    filteredList() {
      return this.products.filter(person => {
        const computedObj = { ...person,
          address: person.location.address,
          zipcode: person.location.zipcode,
          city: person.location.city,
          lng: person.location.lng,
          lat: person.location.lat
        };
        return Object.keys(computedObj).some(key => ('' + computedObj[key]).toLowerCase().includes(this.search.toLowerCase()));
      });
    }

  }
};
