import cartProducts from '../components/cartTest.vue';
import testProducts from '../components/testProducts.vue';
export default {
  name: "TestView",
  components: {
    testProducts,
    cartProducts
  },

  data() {
    return {
      cart: []
    };
  },

  methods: {
    addToCart(product) {
      this.cart.push(product);
      localStorage.setItem('cart', JSON.stringify(this.cart));
    }

  },

  mounted() {
    // بازیابی اطلاعات سبد خرید از local storage
    const savedCart = localStorage.getItem('cart');

    if (savedCart) {
      this.cart = JSON.parse(savedCart);
    }
  }

};
