import "core-js/modules/es.array.includes.js";
export default {
  name: "ProductsView",

  data() {
    return {
      search: ''
    };
  },

  computed: {
    products() {
      return this.$store.state.products;
    },

    filteredList() {
      return this.products.filter(person => {
        const computedObj = {
          person,
          id: person.id,
          title: person.title,
          price: person.price,
          description: person.description,
          category: person.category,
          image: person.image,
          rate: person.rating.rate,
          count: person.rating.count
        };
        return Object.keys(computedObj).some(key => ('' + computedObj[key]).toLowerCase().includes(this.search.toLowerCase()));
      });
    }

  }
};
